import { Component } from "@angular/core";
import { Observable, Subscriber } from "rxjs";

@Component({
    selector: 'app-eventos-create',
    templateUrl: './eventos.component-create.html',
    styleUrls: ['./eventos.component-create.scss']
})
export class EventosCreate {

  localUrl: any;
  file?: File;
  foto: string = "";

    ngOnInit(): void {

    }

    uploadFile() {
      if (this.file != undefined) {}
    }

    onChange(event: any) {
      if(event.target.files && event.target.files[0]) {
        const file = event.target.files[0];
        this.convertToBase64(file);
      }
    }

    readFile(file: File, subscriber: Subscriber<any>){
      const filereader = new FileReader();
      filereader.readAsDataURL(file);

      filereader.onload=()=>{
        subscriber.next(filereader.result);
        subscriber.complete();
      }

      filereader.onerror=(error)=>{
        subscriber.error(error);
        subscriber.complete();
      }
    }

    convertToBase64(file: File){
      const observable = new Observable((subscriber: Subscriber<any>)=>{
        this.readFile(file, subscriber);
      });
      observable.subscribe((f)=>{
        this.foto = f;
      })
    }

    selectFile(event: any) {
      this.file = <File>event.target.files[0];
      console.log(this.file);
      if (event.target.files && event.target.files[0]) {
        var reader = new FileReader();
        reader.onload = (event: any) => {
          this.localUrl = event.target.result;
        }
        reader.readAsDataURL(event.target.files[0]);
      }
    }
}

<div class="evento-create">

    <div class="evento-create-main">

        <h3>Cadastro do Evento</h3>
        <hr>

        <div class="form-main-1">

            <div class="form-1">
                <!-- Foto do Evento -->
                <div id="imgLogo">
                  <img  [src]="foto" alt="logo do evento">
                </div>
                <label class="btn btn-warning btn-upload" >
                  Selecionar Imagem
                  <input
                  id="imgUrl"
                  type="file"
                  style="display: none;"
                  (change)="onChange($event)"
                  />
                </label>
            </div>

            <div class="form-2">
                <!-- Campos do formulário -->

                <div class="form-group">
                    <label class="form-label">Tema</label>
                    <input type="text" class="form-control" />
                </div>

                <div class="form-group">
                    <label class="form-label">Local</label>
                    <input type="text" class="form-control" />
                </div>

                <div class="form-group">
                    <label class="form-label">Data</label>
                    <input type="text" class="form-control" />
                </div>

                <div class="form-group">
                    <label class="form-label">E-mail</label>
                    <input type="text" class="form-control" />
                </div>

                <div class="form-group">
                    <label class="form-label">Valor</label>
                    <input type="text" class="form-control" />
                </div>
            </div>
        </div>

        <div class="form-group">
            <label>Conteúdo Programatico</label>
            <textarea rows="10" class="form-control" placeholder="Separar itens usando o caractere #, por exemplo:  1 - Introdução ao Java # 2 - Orientação a Objetos no Java # 3 - Conexão de Banco de Dados no Java" ></textarea>
        </div>

        <!--Barra de pesquisa -->
        <div class="pesquisa-palestrante">
            <div class="pesquisa">
                <input type="text" class="form-control text-center" placeholder="palestrante.." >
                <button class="btn btn-warning">
                    <i class="fa fa-search"></i>
                </button>
            </div>
        </div>
        <hr>

        <div class="list-search-cardi">

            <div class="search-card">
                <img src="../../../assets/me.png" alt="foto palestrante">
                <p>
                    <span>João Santos</span>
                    <span>Desenvolvedor Full Stack</span>
                </p>
                <button class="btn btn-link">Adicionar</button>
            </div>
        </div>


        <h6>Já cadastrados:</h6>
        <div class="list-palestrnates">

        </div>

    </div>

</div>
