import { HttpClient } from '@angular/common/http';
import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-eventos',
  templateUrl: './eventos.component.html',
  styleUrls: ['./eventos.component.scss']
})
export class EventosComponent {

  public eventos: any = [];
  public eventosFiltrados: any = [];
  mostrarImagens: boolean = true;
  //private _pesquisar: string = '';
  //@Input() _pesquisar: string = ''
  private _pesquisar: string = ''

  constructor(private service: HttpClient){}

  public set pesquisar(texto:  string){
    this._pesquisar = texto;    
    var campo = texto.toLowerCase();
    this.eventosFiltrados = this._pesquisar ? this.filtrarEventos(campo) : this.eventos;
  }

  filtrarEventos(texto: string) : any {
    return this.eventos.filter(
      ((evento: any) => evento.tema.toLocaleLowerCase().indexOf(texto.toLocaleLowerCase()) !== -1)
    )
  }

  public get pesquisar() {
    return this._pesquisar;
  }

  ngOnInit(): void {
    this.getEventos();
  }

  public toggleImage() {
    this.mostrarImagens = !this.mostrarImagens;
  }

  public getEventos(): void {
    this.service.get('http://localhost:5000/api/evento').subscribe(
      response => {
        this.eventos = response;
        //this.eventosFiltrados = this.eventos;
      },
      error => console.log(error)
    )
  }
}
<div class="container-fluid">

  <div class="page-header">

    <div class="titulo-evento">
      <h3>Lista de Eventos</h3>
      <a class="btn shadow">Novo +</a>
    </div>

    <hr>

    <div class="barra-pesquisa">
        <div class="pesquisa">
            <input
            type="text"
            class="form-control"
            [(ngModel)]="pesquisar"
          />
          <!-- <button type="button">
            <i class="fa fa-search"></i>
          </button> -->
        </div>
    </div>  

    <h4>Filtro: {{pesquisar}}</h4>

  </div>

  <div class="alert alert-success" *ngIf="eventos.length == 0" >
    Nenhum evento encontrado!
  </div>

  <button (click)="toggleImage()" class="btn btn-outline-danger mb-2">
    {{!mostrarImagens ? "Exibir Imagens" : "Ocultar Imagens"}}
  </button>

  <div class="card-list">
    <div class="card shadow p-4 mb-4 bg-white" *ngFor="let evento of eventosFiltrados">
      <div class="card-body" >
        <img class="card-img-top"
          alt="Não encontrada"
          [collapse]="!mostrarImagens"
          src={{evento.imageUrl}}
          [isAnimated]="true" style="max-height: 200px;" />

          <u class="card-text evento">
            <li><strong>Local: </strong> {{evento.local}}</li>
            <li><strong>Tema: </strong> {{evento.tema}}</li>
            <li><strong>Data: </strong> {{evento.data}}</li>
            <li><strong>Qtd. Pessoas: </strong> {{evento.qtdePessoas}}</li>
            <li><strong>Lote: </strong> {{evento.lote}}</li>
          </u>
      </div>
    </div>
  </div>

</div>
