import { HttpClient } from '@angular/common/http';
import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-eventos',
  templateUrl: './eventos.component.html',
  styleUrls: ['./eventos.component.scss']
})
export class EventosComponent {

  public eventos: any = [];
  mostrarImagens: boolean = true;
  @Input() listaEvento: string = ''

  constructor(private service: HttpClient){}

  ngOnInit(): void {
    this.getEventos();
  }

  public toggleImage() {
    this.mostrarImagens = !this.mostrarImagens;
  }

  public getEventos(): void {
    this.service.get('http://localhost:5000/api/evento').subscribe(
      response => this.eventos = response,
      error => console.log(error)
    )
  }
}
<div class="container-fluid">
  <h3>Eventos</h3>
  <hr>

  <div class="mb-2">
    <div class="row">
      <div class="col">
        <input
          type="text"
          class="form-control"
          [(ngModel)]="listaEvento"
        />
      </div>
      <div class="col">
        <button type="button" class="btn btn-outline-success">Buscar</button>
      </div>
    </div>
  </div>

  <h4>Filtro: {{listaEvento}}</h4>

  <div class="alert alert-success" *ngIf="eventos.length == 0" >
    Nenhum evento encontrado!
  </div>

  <button (click)="toggleImage()" class="btn btn-outline-light">
    {{!mostrarImagens ? "Exibir" : "Ocultar"}}
  </button>


  <div class="row">
    <div class="card-body col-sm-12 col-md-6 col-lg-3 col-xl-3" *ngFor="let evento of eventos" >
      <img class="card-img-top"
      [collapse]="!mostrarImagens"
      src={{evento.imageUrl}}
      [isAnimated]="true"
      alt="Não encontrada"
      [style.width.px]="100" />
        <u class="card-text evento">
          <li><strong>Local: </strong> {{evento.local}}</li>
          <li><strong>Tema: </strong> {{evento.tema}}</li>
          <li><strong>Data: </strong> {{evento.data}}</li>
          <li><strong>Qtd. Pessoas: </strong> {{evento.qtdePessoas}}</li>
          <li><strong>Lote: </strong> {{evento.lote}}</li>
        </u>
    </div>
  </div>

</div>
